<html><head><?php$con = mysqli_connect('127.0.0.1','root','','sensors');if (!$con) {    die('Could not connect: ' . mysqli_error($con));}		$nodeid= $_GET['id'];$senserate = $_GET['sr'];$sensortype = $_GET['st'];$alarmthreshold = $_GET['at'];$alarmmode = $_GET['am'];$sock = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);    $sql="SELECT id,nodename FROM nodes where id='".$nodeid."'";$result = mysqli_query($con,$sql);while ($row = mysqli_fetch_array($result)){$nodename=$row['nodename'];        }if ($senserate!=""){$command=$nodename.":O:SR:".$sensortype.":".$senserate;socket_sendto($sock, $command, strlen($command), 0, '127.0.0.1', 6666);						if ($senserate==1){							$helper="1000";						}else if ($senserate==2){							$helper="3000";						}else if ($senserate==3){							$helper="5000";						}else if ($senserate==4){							$helper="30000";						}else if ($senserate==5){							$helper="60000";						}else if ($senserate==6){							$helper="300000";						}else if ($senserate==7){							$helper="600000";						}$sql="update sensors set senserate='".$helper."' where nodeid='".$nodeid."' and sensortype='".$sensortype."'";$result = mysqli_query($con,$sql);}if ($alarmthreshold!=""){$command=$nodename.":O:AT:".$sensortype.":".$alarmthreshold;socket_sendto($sock, $command, strlen($command), 0, '127.0.0.1', 6666);if ($sensortype=="temp"){						if ($alarmthreshold==1){							$helper="0.1";						}else if ($alarmthreshold==2){							$helper="0.5";						}else if ($alarmthreshold==3){							$helper="1";						}else if ($alarmthreshold==4){							$helper="2";						}$sql="update sensors set alarmthreshold='".$helper."' where nodeid='".$nodeid."' and sensortype='".$sensortype."'";$result = mysqli_query($con,$sql);}else if ($sensortype=="light"){						if ($alarmthreshold==1){							$helper="30";						}else if ($alarmthreshold==2){							$helper="100";						}else if ($alarmthreshold==3){							$helper="150";						}else if ($alarmthreshold==4){							$helper="300";						}$sql="update sensors set alarmthreshold='".$helper."' where nodeid='".$nodeid."' and sensortype='".$sensortype."'";$result = mysqli_query($con,$sql);}}if ($alarmmode!=""){$command=$nodename.":O:AM:".$alarmmode;socket_sendto($sock, $command, strlen($command), 0, '127.0.0.1', 6666);if ($alarmmode=="ON"){							$helper="1";						}else if ($alarmmode=="OFF"){							$helper="0";						}$sql="update settings set alarmmode='".$helper."' where nodeid='".$nodeid."'";$result = mysqli_query($con,$sql);}												socket_close($sock);								mysqli_close($con);										?><meta http-equiv="refresh" content="0;url=settings.php" /></head></html>